test_that("Adding rows (SNPs) to replicate df", {
  df1_4alleles = data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Allele.3 = c("G", "G", "G"), Allele.4 = c("T", "T", "T"), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Height.3 = c(10, 10, 10), Height.4 = c(5, 5, 5), Total_Reads = c(215, 215, 215))
  df2_4alleles = data.frame(`Sample Name` = c("A01", "A01"), Marker = c("rs3843794", "rs5979573"), Allele.1 = c("A", "A"), Allele.2 = c("C", "C"), Allele.3 = c("G", "G"), Allele.4 = c("T", "T"), Height.1 = c(100, 100), Height.2 = c(100, 100), Height.3 = c(10, 10), Height.4 = c(5, 5), Total_Reads = c(215, 215))
  expect_equal(add_rows(df1_4alleles, df2_4alleles), data.frame(`Sample Name` = c("A01"), Marker = c("rs6530168"), Allele.1 = c("A"), Allele.2 = c("C"), Allele.3 = c("G"), Allele.4 = c("T"), Height.1 = c(0), Height.2 = c(0), Height.3 = c(0), Height.4 = c(0), Total_Reads = c(0)))
  df1_3alleles = data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Allele.3 = c("G", "G", "G"), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Height.3 = c(10, 10, 10), Total_Reads = c(215, 215, 215))
  df2_3alleles = data.frame(`Sample Name` = c("A01", "A01"), Marker = c("rs3843794", "rs5979573"), Allele.1 = c("A", "A"), Allele.2 = c("C", "C"), Allele.3 = c("G", "G"), Height.1 = c(100, 100), Height.2 = c(100, 100), Height.3 = c(10, 10), Total_Reads = c(215, 215))
  expect_equal(add_rows(df1_3alleles, df2_3alleles), data.frame(`Sample Name` = c("A01"), Marker = c("rs6530168"), Allele.1 = c("A"), Allele.2 = c("C"), Allele.3 = c("G"), Height.1 = c(0), Height.2 = c(0), Height.3 = c(0), Total_Reads = c(0)))
  df1_2alleles = data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Total_Reads = c(215, 215, 215))
  df2_2alleles = data.frame(`Sample Name` = c("A01", "A01"), Marker = c("rs3843794", "rs5979573"), Allele.1 = c("A", "A"), Allele.2 = c("C", "C"), Height.1 = c(100, 100), Height.2 = c(100, 100), Total_Reads = c(215, 215))
  expect_equal(add_rows(df1_2alleles, df2_2alleles), data.frame(`Sample Name` = c("A01"), Marker = c("rs6530168"), Allele.1 = c("A"), Allele.2 = c("C"), Height.1 = c(0), Height.2 = c(0), Total_Reads = c(0)))
})
