test_that("Adding extra columns as necessary", {
  df1 = data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Allele.3 = c("G", "G", "G"), Allele.4 = c("T", "T", "T"), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Height.3 = c(10, 10, 10), Height.4 = c(5, 5, 5), Total_Reads = c(215, 215, 215))
  df2a = data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Allele.3 = c("G", "G", "G"), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Height.3 = c(10, 10, 10), Total_Reads = c(215, 215, 215))
  df2b = data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Total_Reads = c(215, 215, 215))
  expect_equal(check_3_or_4_col(df1, df2a), data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Allele.3 = c("G", "G", "G"), Allele.4 = c(0, 0, 0), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Height.3 = c(10, 10, 10), Height.4 = c(0, 0, 0), Total_Reads = c(215, 215, 215)))
  expect_equal(check_3_or_4_col(df1, df2b), data.frame(`Sample Name` = c("A01", "A01", "A01"), Marker = c("rs3843794", "rs5979573", "rs6530168"), Allele.1 = c("A", "A", "A"), Allele.2 = c("C", "C", "C"), Allele.3 = c(0, 0, 0), Allele.4 = c(0, 0, 0), Height.1 = c(100, 100, 100), Height.2 = c(100, 100, 100), Height.3 = c(0, 0, 0), Height.4 = c(0, 0, 0), Total_Reads = c(215, 215, 215)))
})
